mapper.select:
SELECT
	id,
	ad_push_detail_id,
	date,
	day_cost,
	day_earn,
	regist_num
FROM t_ad_push_cost
WHERE del_flag = #{DEL_FLAG_NORMAL}

	AND ad_push_detail_id = #{adPushDetailId}


	AND date  >=  #{startDate}


	AND date  <=  #{endDate}



		ORDER BY ${page.orderBy}


		ORDER BY date DESC

mapper.update:
UPDATE t_ad_push_cost SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE t_ad_push_cost SET
del_flag = #{DEL_FLAG_DELETE}
WHERE ad_push_detail_id = #{adPushDetailId}
