mapper.select:
SELECT id, ckAppId, ckChannelId, versionName, mmappid, payType, area, timeframe, isUse, create_date as createDate, create_by as createBy FROM app_pay_list a WHERE 1=1
and a.ckAppId = #{ckAppId}
and a.ckChannelId = #{ckChannelId}

mapper.update:
    	UPDATE app_pay_list SET
    	 payType = #{payType},
    	 ckChannelId =#{ckChannelId},
    	 payType =#{payType},
    	 isUse =#{isUse}
		WHERE id=#{id}
