mapper.select:
SELECT
	id,
	ckAppId,
	code,
	status
FROM t_gamecode 
WHERE code = #{code}

	AND ckAppId = #{ckAppId}

and status = '0';

select CONCAT(b.name,'(',a.ckAppID,')')as ckAppId , a.code as code , a.status as status from t_gamecode a LEFT JOIN app_ck b on a.ckAppID = b.ckapp_id LEFT JOIN t_gamecode_ver c on a.verId = c.id where c.status = '0'
AND a.ckAppId = #{ckAppId}
ORDER BY ${page.orderBy}
ORDER BY a.ckAppId

select a.id as id, CONCAT(b.name,'(',a.ckapp_id,')')as ckAppId , a.date as date, a.remark as remark, a.status as status from t_gamecode_ver a LEFT JOIN app_ck b on a.ckapp_id = b.ckapp_id where 1 = 1
AND a.ckapp_id = #{ckAppId}
ORDER BY ${page.orderBy}
ORDER BY ckAppId

SELECT a.id, a.ckAppId, a.code FROM t_gamecode a JOIN t_gamecode_ver b ON a.verId = b.id WHERE a.status = '0' AND b.status = '0'
AND ckAppId = #{ckAppId}
limit #{randomInt},1

mapper.update:
UPDATE t_gamecode set
status = '1'
WHERE id = #{id}
and status = '0';

UPDATE t_gamecode_ver set
status = #{status}
WHERE id = #{id}

mapper.select:
SELECT id, ckapp_id as ckAppId, code, phone_num as phoneNum, date, status FROM t_gamecode_log WHERE status = '0'
AND phone_num = #{phoneNum}
AND ckapp_id = #{ckAppId}
AND date >= #{startDate}
AND date <= #{endDate}
limit 0,1
